from core.models.base import BaseEntityfrom django.db import modelsfrom django.contrib.auth.models import Userfrom core.models import Publicationclass Article(BaseEntity):    headline = models.CharField(max_length=120)    user = models.ForeignKey(User, on_delete=models.CASCADE, related_name='author')    body = models.TextField()    publications = models.ManyToManyField(Publication)    class Meta:        ordering = ['headline']    def __str__(self):        return self.headline[:20]