from datetime import datetimefrom django.contrib.auth.models import Userfrom drf_role.models import Rolefrom rest_framework import serializersfrom rest_framework.exceptions import ValidationErrorfrom core.models.profile import Profileclass UserSerializer(serializers.ModelSerializer):    class Meta:        model = User        fields = ('id', 'username', 'is_active')class RoleSerializer(serializers.ModelSerializer):    class Meta:        model = Role        fields = ('id', 'name', 'type')class ProfileSerializer(serializers.ModelSerializer):    user = UserSerializer(read_only=True)    role = RoleSerializer(read_only=True)    class Meta:        model = Profile        fields = (            'id', 'user', 'role', 'date_of_birth',            'gender', 'mobile', 'address', 'created_at', 'updated_at'        )